<script>
export default {
    name: 'Slider',
    data() {
        return {
            currentCarousel: 0,
            carousels: [
                'data:image/jpeg;base64,',
                'data:image/jpeg;base64,',
                'data:image/jpeg;base64,',
            ],
        }
    },
    watch: {
        // eslint-disable-next-line
        currentCarousel(newVal, oldVal) {
        if(newVal >= this.carousels.length) {
            this.currentCarousel = 0
        }
        if(newVal < 0) {
            this.currentCarousel = this.carousels.length - 1;
        }
    } 
  }, 
}
</script>

<style lang="css" scoped>

</style>

<template>
    <div id="carouselExampleControls" class="carousel slide bg-primary" data-ride="carousel">
        <div class="carousel-inner" v-for="(carousel, index) in carousels" :key="carousel">
          <div class="carousel-item" :class="currentCarousel == index? 'active': null">
            <img class="d-block w-100" :src="carousel" alt="First slide">
          </div>
        </div>
        <a class="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev" 
        @click="currentCarousel--">
          <span class="carousel-control-prev-icon" aria-hidden="true"></span>
          <span class="sr-only">Previous</span>
        </a>
        <a class="carousel-control-next" href="#carouselExampleCon+trols" role="button" data-slide="next" 
        @click="currentCarousel++">
          <span class="carousel-control-next-icon" aria-hidden="true"></span>
          <span class="sr-only">Next</span>
        </a>
      </div>
</template>